import { ChartData, ChartDataset } from "chart.js";
import { IBarChartData, IBarChartDataset, IBarChartOptions, IBarChartPoint } from "../barChart/interfaces";
import { ILineChartData, ILineChartDataset, ILineChartOptions, ILineChartPoint } from "../lineChart/interfaces";
import { IPieChartData } from "../pieChart/interfaces";
import { BarChartType, LineChartType, PieChartType } from "../chart";
export type AccessibleChartData = ILineChartData | IBarChartData | IPieChartData | ChartData<"line">;
export type AccessibleChartType = LineChartType | BarChartType | PieChartType | "line-mini";
export type AccessibleChartDataset = ILineChartDataset | IBarChartDataset | ChartDataset<"line">;
export interface IAccessibleChart {
    data: AccessibleChartData;
    type: AccessibleChartType;
    options?: ILineChartOptions | IBarChartOptions;
}
export interface IChartTrend {
    trendDirection: "increases" | "decreases" | "flat" | "complex" | "undefined";
    summary: string;
}
export interface IExtremePoints {
    highest: ILineChartPoint | IBarChartPoint | null;
    lowest: ILineChartPoint | IBarChartPoint | null;
}
export interface IRegressionLine {
    slope: number;
    yIntercept: number;
}
export interface IDeviatingPoint {
    x: number | string;
    y: number;
    residual: number;
    isSignificantlyHigh: boolean;
    isSignificantlyLow: boolean;
}
export interface IAccessiblePoint {
    x: number;
    y: number;
    originX: string | number | Date;
}
export interface IScoredDataPoint {
    point: ILineChartPoint;
    peakHeight: number;
    valleyHeight: number;
    peakDominance: number;
    leftPeakDominance?: number;
    rightPeakDominance?: number;
    valleyDominance: number;
    leftValleyDominance?: number;
    rightValleyDominance?: number;
}
