import { ChartData, ChartDataset, ChartOptions, PluginOptionsByType } from "chart.js/auto";
interface IBarChartPluginOptions extends Partial<PluginOptionsByType<"bar">> {
}
export interface IBarChartOptions extends ChartOptions<"bar"> {
    plugins?: IBarChartPluginOptions;
}
export interface IBarChartPoint {
    x: number | string;
    y: number;
}
export type IBarChartDataset = ChartDataset<"bar", IBarChartPoint[]>;
export interface IBarChartData extends Omit<ChartData<"bar">, "datasets" | "labels"> {
    datasets: IBarChartDataset[];
}
export {};
