import { ChartData, ChartDataset, ChartOptions, PluginOptionsByType } from "chart.js/auto";
interface ILineChartPluginOptions extends Partial<PluginOptionsByType<"line">> {
}
export interface ILineChartOptions extends ChartOptions<"line"> {
    plugins?: ILineChartPluginOptions;
}
export interface ILineChartPoint {
    x: number | string | Date;
    y: number | null;
}
export type ILineChartNativeDataset = ChartDataset<"line", ILineChartPoint[]>;
export interface ILineChartDataset extends ILineChartNativeDataset {
    trendData?: ILineChartPoint[];
    parentIndex?: number;
}
export interface ILineChartData extends Omit<ChartData<"line">, "datasets" | "labels"> {
    datasets: ILineChartDataset[];
}
export {};
