import { ReactNode } from "react";
import { ColumnSortDirection } from "../table/table";
export interface IListColumnMeta<T, N = T> {
    readonly defaultWidth?: number;
    readonly defaultValue?: string;
    readonly lazyLoad?: boolean;
    readonly visibleOnHover?: boolean;
    readonly initialSortDirection?: ColumnSortDirection;
    readonly renderPlaceholder?: (entity: T | N) => ReactNode;
    readonly renderFeedPlaceholder?: (title: ReactNode, entity: T | N) => [ReactNode, ReactNode];
}
export interface IListColumn<T, N = T> {
    readonly id: string;
    readonly name?: string | keyof T;
    readonly title: string;
    meta?: IListColumnMeta<T, N>;
    columnComponent?: IColumnComponent<T, N>;
    wrappers?: IListColumnWrapper<T, N>[];
    colspan?: undefined | number | ((entity: T | N) => number | undefined);
}
export type TRenderResult = ReactNode | PromiseLike<ReactNode | undefined> | undefined;
type TFeedResultBasic = [
    ReactNode,
    TRenderResult
];
export type TFeedResult = TFeedResultBasic | Readonly<TFeedResultBasic>;
export interface IListColumnBasic<T, N = T> {
    renderHeader?: (title: string) => ReactNode;
    render: (entity: T | N, isNested?: boolean) => TRenderResult;
    renderFeed?: (title: string, entity: T | N, isNested?: boolean) => TFeedResult;
}
export interface IColumnComponentNested<T, N> {
    renderHeader?: (title: string) => ReactNode;
    render: (entity: T, isNested?: boolean) => TRenderResult;
    renderFeed?: (title: string, entity: T, isNested?: boolean) => TFeedResult;
    renderNested: (entity: N) => TRenderResult;
}
export type IColumnComponent<T, N = T> = IColumnComponentNested<T, N> | IListColumnBasic<T, N>;
export interface IListColumnStrict<T, N> {
    renderHeader: (title: string) => ReactNode;
    render: (entity: T | N, isNested?: boolean) => TRenderResult;
    renderFeed: (title: string, entity: T | N, isNested?: boolean) => TFeedResult;
}
interface IListColumnWrapperBasic<T, N> {
    renderHeader: (title: ReactNode) => ReactNode;
    render: (result: ReactNode, entity: T | N, isNested?: boolean) => ReactNode;
    name?: string;
}
export type IListColumnWrapper<T, N = T> = IListColumnWrapperBasic<T, N>;
export {};
