export interface IFiltersBarItemCommonProps {
    id: string;
    sidePanelTitle: string;
    showInSidePanel?: boolean;
    hideInBar?: boolean;
    useSidePanelView?: boolean;
}
export interface IFiltersBarItem<State = unknown, Props = unknown> extends IFiltersBarItemCommonProps {
    state: State;
    props?: Props;
    onChange: (state: State) => void;
}
export interface ICustomFiltersBarItem<T> extends Pick<IFiltersBarItem, "id" | "showInSidePanel" | "useSidePanelView" | "hideInBar"> {
    state: {
        value: T;
    };
    onChange: (state: {
        value: T;
    }) => void;
}
